open class DbMallwebHelper(var context: Context, DB_NAME: String = "MallwebDB", DB_VERSION: Int = 2): SQLiteOpenHelper(context, DB_NAME, null, DB_VERSION) {

    private val createTableClients = "CREATE TABLE IF NOT EXISTS clients (id INTEGER PRIMARY KEY AUTOINCREMENT, email TEXT NOT NULL, name TEXT NOT NULL, lastname TEXT NOT NULL, birthday TEXT, codarea TEXT, numCelular TEXT, dni TEXT NOT NULL, cuit TEXT NOT NULL, wantABill TEXT NOT NULL)"
    private val createTableAddress = "CREATE TABLE IF NOT EXISTS address (idClient INTEGER PRIMARY KEY REFERENCES clients(id), street TEXT NOT NULL, number TEXT NOT NULL, floor TEXT NOT NULL, door TEXT NOT NULL, postalCode TEXT NOT NULL, province TEXT NOT NULL, locality TEXT NOT NULL)"
    private val createTableOrders = "CREATE TABLE IF NOT EXISTS orders (idClient INTEGER REFERENCES clients(id), numberOrder INTEGER PRIMARY KEY AUTOINCREMENT, date DATE NOT NULL, total DOUBLE NOT NULL, state TEXT NOT NULL, shipping TEXT NOT NULL, actions TEXT NOT NULL)"
    private val createTableBill = "CREATE TABLE IF NOT EXISTS bill (numBill INTEGER PRIMARY KEY AUTOINCREMENT, idClient INTEGER REFERENCES clients(id), date DATE NOT NULL, numPayment INTEGER REFERENCES payMethod(numPayment))"
    private val createTablePayMethod = "CREATE TABLE IF NOT EXISTS payMethod (numPayment INTEGER PRIMARY KEY AUTOINCREMENT, namePayment TEXT NOT NULL, details TEXT)"
    private val createTableDetails = "CREATE TABLE IF NOT EXISTS details (numDetails INTEGER NOT NULL, numBill INTEGER REFERENCES bill(numBill), idProduct INTEGER REFERENCES products(idProduct), cant INTEGER NOT NULL, price DOUBLE NOT NULL, PRIMARY KEY (numDetails, numBill))"
    private val createTableProducts = "CREATE TABLE IF NOT EXISTS products (idProduct INTEGER PRIMARY KEY, codFab TEXT NOT NULL, name TEXT NOT NULL, idCategory INTEGER REFERENCES category(idCategory), idBrand REFERENCES brands(idBrand), stock INTEGER NOT NULL, price DOUBLE NOT NULL, image INTEGER NOT NULL)"
    private val createTableCategory = "CREATE TABLE IF NOT EXISTS category (idCategory INTEGER PRIMARY KEY, name TEXT NOT NULL)"
    private val createTableBrands = "CREATE TABLE IF NOT EXISTS brands (idBrand INTEGER PRIMARY KEY, name TEXT NOT NULL, image INTEGER NOT NULL)"



    override fun onCreate(db: SQLiteDatabase?) {
        db?.execSQL(createTableClients)
        db?.execSQL(createTableAddress)
        db?.execSQL(createTableOrders)
        db?.execSQL(createTableBill)
        db?.execSQL(createTablePayMethod)
        db?.execSQL(createTableDetails)
        db?.execSQL(createTableProducts)
        db?.execSQL(createTableCategory)
        db?.execSQL(createTableBrands)
        loadData(db)
    }

    override fun onUpgrade(db: SQLiteDatabase?, oldVersion: Int, newVersion: Int) {
        db?.execSQL("DROP TABLE clients")
        db?.execSQL("DROP TABLE address")
        db?.execSQL("DROP TABLE orders")
        db?.execSQL("DROP TABLE bill")
        db?.execSQL("DROP TABLE payMethod")
        db?.execSQL("DROP TABLE details")
        db?.execSQL("DROP TABLE products")
        db?.execSQL("DROP TABLE category")
        db?.execSQL("DROP TABLE brands")
        onCreate(db)
    }

